CM.TypedBufferInputStream.prototype.getUtf8String = function(byteLength) {
  var out, i, len, c;
  var char2, char3;

  out = "";
  len = byteLength;
  i = 0;
  while(i < byteLength) {
    c = this.getUint8();
    i++;

    switch(c >> 4)
    {
      case 0: case 1: case 2: case 3: case 4: case 5: case 6: case 7:
      // 0xxxxxxx
      out += String.fromCharCode(c);
      break;
      case 12: case 13:
      // 110x xxxx   10xx xxxx
      char2 = this.getUint8();
      i++;
      out += String.fromCharCode(((c & 0x1F) << 6) | (char2 & 0x3F));
      break;
      case 14:
        // 1110 xxxx  10xx xxxx  10xx xxxx
        char2 = this.getUint8();
        i++;
        char3 = this.getUint8();
        i++;
        out += String.fromCharCode(((c & 0x0F) << 12) |
          ((char2 & 0x3F) << 6) |
          ((char3 & 0x3F) << 0));
        break;
    }
  }

  return out;
};

CM.TypedBufferInputStream.prototype.getUtf16String = function(byteLength) {
  var string = '';
  var shortLength = byteLength / 2;
  for (var i = 0; i < shortLength; i++) {
    string += String.fromCharCode(this.getUint16());
  }
  return string;
};
